import { createSelector } from '@ngrx/store';
export const selectPieces = state => state.pieces;
export const selectTurns = state => state.turns;
export const selectBoardSquares = state => state.boardSquares;
export const selectGameStatus = state => state.gameStatus;
export const selectSelection = state => state.selection;
export const selectCastle = state => state.castle;
export const getPieces = createSelector(selectPieces, pieces => pieces);
export const getTurns = createSelector(selectTurns, turns => turns);
export const getBoardSquares = createSelector(selectBoardSquares, boardSquares => boardSquares);
export const getGameStatus = createSelector(selectGameStatus, check => check);
export const getSelection = createSelector(selectSelection, selection => selection);
export const getCastle = createSelector(selectCastle, castle => castle);
